sys_script_client.script:
function onLoad() {
	//Type appropriate comment here, and begin script below
	if(g_form.isNewRecord()) {
		g_form.setValue('assignment_group', g_scratchpad.assignment_group);
		g_form.setValue('subject', g_scratchpad.subject);
		g_form.setValue('assigned_to', g_scratchpad.assigned_to);
		g_form.setValue('book', g_scratchpad.book);
		g_form.setValue('pages', g_scratchpad.pages);
		g_form.setValue('due_date', g_scratchpad.due_date);
		g_form.setValue('expected_start', g_scratchpad.expected_start);
		g_form.setValue('short_description', g_scratchpad.short_description);

	}
}
